query BlogPostQuery(
  $index: Int
) {
  content: allMarkdownRemark(sort: {order: DESC, fields: frontmatter___date}, skip: $index, limit: 1) {
    edges {
      node {
        id
        html
        tableOfContents
        excerpt
        ...post
      }
      previous {
        ...post
      }
      next {
        ...post
      }
    }
  }
  latestPosts: allMarkdownRemark(sort: {order: DESC, fields: frontmatter___date}, limit: 6) {
    totalCount
    edges {
      node {
        ...post
      }
    }
  }
}

fragment post on MarkdownRemark {
  fields {
    slug
  }
  frontmatter {
    id
    title
    slug
    date
    headerImage
  }
}

query tagQuery(
  $tag: [String!]
) {
  allMarkdownRemark(sort: {order: DESC, fields: frontmatter___date}, filter: {frontmatter: {tags: {in: $tag}}}) {
    edges {
      node {
        id
        frontmatter {
          id
          url: slug
          title
          date
          tags
          headerImage
          description
        }
      }
    }
  }
}

